import { createFeatureSelector, createSelector } from '@ngrx/store';
export const selectCartState = createFeatureSelector('cart');
export const selectCartItems = createSelector(selectCartState, state => state.items);
export const selectCartItemCount = createSelector(selectCartItems, items => items.reduce((total, item) => total + item.quantity, 0));
export const selectCartTotal = createSelector(selectCartItems, items => items.reduce((total, item) => total + item.product.price * item.quantity, 0));
export const selectCartItemById = productId => createSelector(selectCartItems, items => items.find(item => item.product.id === productId));
